import { useEffect, useState } from "react";
import { Link } from "react-router-dom"; // تصحيح استيراد Link

function Courses() { // تصحيح اسم الدالة
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        getAllCourses()
    }, []);

    const getAllCourses = () => {
        fetch('http://localhost:9000/corsses') // تأكد من أن الـ API endpoint صحيح
        .then((res) => res.json())
        .then((data) => {
           
        })
        .catch((error) => {
            console.error('Error fetching courses:', error);
        });
    }

    const deletCourse =(courseId) => { 
        fetch(`http://localhost:9000/corsses/${courseId}`, {
            method: 'DELETE'
        }).then((response) => response.json())
        .then((data) => { 
            console.log('Course deleted:', data)
         
         })
        .catch((error) => {
            console.error('Error deleting course:', error);
        });  // حذف دورة على الأقل مرة لأنه متا�� فقط عند حذف الدورة الأخيرة من القا��دة البيانية
     }

    return (
        <>
            <h1>Courses Page</h1>

            <Link to={'/courses/add'} className="btn btn-success mt-3">Add New Course</Link>

            <table className="table table-striped mt-5">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Course Title</th>
                        <th>Coach's Name</th>
                        <th>Description</th>
                        <th>Price $</th>
                        <th>Operations</th>
                    </tr>
                </thead>
                <tbody>
                    {courses.map((course) => (
                        <tr key={course.id}> {/* إضافة key */}
                            <td>{course.id}</td>
                            <td>{course.Title}</td>
                            <td>{course.name}</td> {/* Coach's name */}
                            <td>{course.description.slice(0, 5)}..</td>
                            <td>{course.price }</td> {/* إذا كانت الخاصية موجودة في البيانات */}
                            <td>
                                <button className="btn btn-danger" onClick={() => deletCourse(course.id)}>Delete</button>
                                <Link to={`/coursses/${course.id}`} className="btn btn-info ">View</Link>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </>
    );
}

export default Courses;
